mapper.select:
SELECT M_NO, M_NM, NICK_NM, M_PW, PHONE, EMAIL, VAC_YN, POST_NO, ADR, DTL_ADR, IMG_ADR
FROM M
WHERE M_NO = #{m_no}

mapper.update:
UPDATE M SET M_NM = #{mNm}, NICK_NM = #{nickNm},
M_PW = #{mPw},
PHONE = #{mPhone}, EMAIL = #{email}, vac_yn=#{vec}, POST_NO = #{cm_postcode}, ADR = #{cm_address}, DTL_ADR = #{cm_detailAddress},
IMG_ADR = #{imgFile}
WHERE M_NO = #{no}
