mapper.select:
SELECT OPT_NO, PROD_NO, PRICE, UNIT
FROM PROD_OPT
WHERE PROD_NO =#{prodNo}

SELECT ATTC_NO,PROD_NO,FILE_ADDR
FROM PROD_ATTC
WHERE PROD_NO = #{prodNo}

mapper.insert:

INSERT INTO ORDER_PROD(ORDER_NO,PROD_NO,OPT_NO,QTY)
SELECT O.ORD_NO as ORD_NO ,#{pNoI},#{oNoI},#{qNoI}
FROM (SELECT ORD_NO,USER_NO,RANK() OVER(PARTITION BY USER_NO ORDER BY ORD_NO DESC) RNK
        		FROM ORD )O
WHERE USER_NO = #{sMNo}
AND RNK= #{noIndex}

mapper.select:
SELECT POINT
FROM M
WHERE M_NO = #{sMNo}

SELECT P.CAT_NO,P.PROD_NO,P.PROD_NM,M.M_NM,O.POST_NO,O.ADR,O.DTL_ADR,TO_CHAR(O.ORD_DT,'YYYY-MM-DD') AS ORD_DT,O.ORD_STAT FROM ORD O INNER JOIN ORDER_PROD OP ON O.ORD_NO = OP.ORDER_NO INNER JOIN PRODUCT P ON OP.PROD_NO = P.PROD_NO INNER JOIN M ON M.M_NO = O.USER_NO WHERE O.USER_NO=#{sMNo}
AND TO_CHAR(O.ORD_DT,'YYYY-MM-DD') BETWEEN #{startDate} AND #{endDate}
ORDER BY O.ORD_NO

SELECT P.PROD_NO,M.M_NM,PC.CAT_NM,P.PROD_NM,P.POINT,P.SALE_STAT,TO_CHAR(P.PROD_DT,'YYYY-MM-DD') AS PROD_DT FROM PRODUCT P INNER JOIN PROD_CAT PC ON P.CAT_NO = PC.CAT_NO INNER JOIN M M ON P.REG_NO = M.M_NO WHERE P.DEL = 1
AND P.PROD_NO = #{searchTxt}
AND PC.CAT_NM LIKE '%' || #{searchTxt} || '%'
AND P.PROD_NM LIKE '%' || #{searchTxt} || '%'
AND P.SALE_STAT = #{searchTxt}

mapper.update:
UPDATE PRODUCT SET PROD_NM = #{prodNm}, 
                   CAT_NO = #{catNo},
                   POINT = #{price},
                   CON = #{con},
                   SALE_STAT = #{saleStat}
WHERE PROD_NO = #{pNo}
