mapper.insert:
INSERT INTO INFO(INFO_NO
			   , WRITER_NO
			   , INFO_TITLE
			   , INFO_SUBHD
			   , CON
			   , REP_IMG
			   , DISP_YN
			   )
		  VALUES(INFO_SEQ.NEXTVAL
		       , #{m_no}
		       , #{info_title}
		       , #{info_subhd}
		       , #{con}
		       , #{imgFile}
		       , #{disp_yn}
		       )

mapper.select:
SELECT INFO.INFO_NO, INFO.INFO_TITLE, INFO.INFO_SUBHD, INFO.DT, INFO.DISP_YN FROM ( SELECT INFO_NO, INFO_TITLE, INFO_SUBHD, DISP_YN, TO_CHAR(DT, 'YYYY-MM-DD') AS DT, ROW_NUMBER() OVER(ORDER BY INFO_NO DESC) AS RNUM FROM INFO WHERE DEL = 1
AND INFO_TITLE LIKE '%' || #{searchTxt} || '%'
AND INFO_SUBHD LIKE '%' || #{searchTxt} || '%'
) INFO WHERE INFO.RNUM BETWEEN #{startCnt} AND #{endCnt}

mapper.update:
		UPDATE INFO SET INFO_TITLE = #{info_title},
              INFO_SUBHD = #{info_subhd},
              CON = #{con},
              REP_IMG = #{imgFile},
              DISP_YN = #{disp_yn}
		WHERE  INFO_NO = #{no}
